import { useEffect } from "react";
import Scrollbar from 'smooth-scrollbar';

var options = {
    damping:0.07,
}

const Scroll = () => {
    useEffect(()=>{
        Scrollbar.init(document.getElementById('layout'), options);
    },[])
    return null
}

export default Scroll
